mapper.select:
select 

from sys_processmaterial
where processID = #{processid,jdbcType=VARCHAR}
  and materialID = #{materialid,jdbcType=VARCHAR}
  and inOrOut = #{inorout,jdbcType=INTEGER}

mapper.delete:
delete from sys_processmaterial
where processID = #{processid,jdbcType=VARCHAR}
  and materialID = #{materialid,jdbcType=VARCHAR}
  and inOrOut = #{inorout,jdbcType=INTEGER}

mapper.update:
update sys_processmaterial


    status = #{status,jdbcType=INTEGER},


where processID = #{processid,jdbcType=VARCHAR}
  and materialID = #{materialid,jdbcType=VARCHAR}
  and inOrOut = #{inorout,jdbcType=INTEGER}

update sys_processmaterial
set status = #{status,jdbcType=INTEGER}
where processID = #{processid,jdbcType=VARCHAR}
  and materialID = #{materialid,jdbcType=VARCHAR}
  and inOrOut = #{inorout,jdbcType=INTEGER}
