mapper.select:
select 

from tb_workorder
where orderID = #{orderid,jdbcType=VARCHAR}
  and plantID = #{plantid,jdbcType=VARCHAR}
  and processID = #{processid,jdbcType=VARCHAR}

mapper.delete:
delete from tb_workorder
where orderID = #{orderid,jdbcType=VARCHAR}
  and plantID = #{plantid,jdbcType=VARCHAR}
  and processID = #{processid,jdbcType=VARCHAR}
